	{name='grass_6',objType='LDImage',class='salita',width=70,height=70,x=620,y=951,xScale=1*1,yScale=1*1,assetName='grass_0',
		rotation=-17,enableDrag=false,anchorX=0.5,anchorY=0.5,alpha=1,isVisible=true,blendMode='normal',
		userProps = {},
		},
	{name='grass_7',objType='LDImage',class='salita',width=70,height=70,x=668,y=935,xScale=1*1,yScale=1*1,assetName='grass_0',
		rotation=-17,enableDrag=false,anchorX=0.5,anchorY=0.5,alpha=1,isVisible=true,blendMode='normal',
		userProps = {},
		},
	{name='grass_8',objType='LDImage',class='salita',width=70,height=95.9238,x=863,y=915,xScale=1*1,yScale=1.37034*1,assetName='grass_0',
		rotation=-17,enableDrag=false,anchorX=0.5,anchorY=0.5,alpha=1,isVisible=true,blendMode='normal',
		userProps = {},
		},
	}
	-- add objects to layer
	for k, objProps in pairs (objects) do
		obj = instance:createObject(level.layers['salita'], objProps ,level.assets)
		if (obj and objProps.userProps) then
			obj.property = {}
			for u, userProp in pairs (objProps.userProps) do
				obj.property[userProp.name] = userProp.value
			end
		end
	end
	instance:insertLayer(level,level.layers['salita'])
	--level.ldVersion=1.1.0	-- Level Director Version
	-- Parallax (after layers)
	level.canvasWidth=1920
	level.canvasHeight=1080
	if level.parallaxEnabled then
		level.parallaxInfinite=false
		level.parallaxDamping=1
		instance:createParallax(level)
	end
	return level
end -- createLevel
return M
